import {Component} from "react";
import './App.css';

// JSX : JavaScript를 확장한 문법 (html 태그를 따옴표로 표현하지 않아도 됨)
class Subject extends Component {
    render() {
        return ( // 하나의 render 안에는 하나의 최상위 html 태그를 정의해야한다
            <header>
                <h1>{this.props.title}</h1>
                {this.props.sub}
            </header>
        );
    }
}

class ToC extends Component {
    render() {
        return (
            <nav>
                <ul>
                    <li><a href="1.html">install</a></li>
                    <li><a href="2.html">coding & run</a></li>
                    <li><a href="3.html">deploy</a></li>
                </ul>
            </nav>
        );
    }
}

class Content extends Component {
    render() {
        return (
            <article>
                <h2>install</h2>
                <ol>
                    <li>npm - nodejs 프로그램 (영구설치)</li>
                    <li>npx  - 임시로 설치해서 삭제하고 사용할때마다 다시 다운하여 늘 최신상태 유지</li>
                </ol>
                ​
                2. coding & run
                npm run start -> src폴더의 코드편집
                ​
                3. deploy
                원본을 그대로 배포하면 파일용량이 크고 보안문제가 있음
                -> ​npm run build 명령 : build 폴더 안에 용량이 줄어든 코드파일 자동생성
                ​-> npm install -g serve : 서브설치 (npm을 통해 설치가능한 간단한 웹서버)
                ​-> serve -s build : 서브를 실행시킬때 build 디렉토리를 루트로 하겠다
            </article>
        );
    }
}

class App_class_nm extends Component {
    render() { // render() : class 안에 소속된 함수는 function을 생략해서 표현
        return (
            <div className="App_css">
                <Subject title="Web" sub="world wide web!"></Subject>
                <Subject title="React" sub="페이스북에서 개발한 자바스크립트 라이브러리"></Subject>
                <ToC></ToC>
                <Content></Content>
            </div>
        );
    }
}

export default App_class_nm;
