import {Component} from "react";

class UpdateContent extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data:this.props.data
        }
    }
    render() {
        return (
            <article>
                <h2>Update</h2>
                <form action="/update_proc" method="post"
                      onSubmit={function (e) {
                        e.preventDefault();
                        this.props.onSubmit(this.state.data.id, e.target.title.value, e.target.desc.value);
                          /* e.target:form에서 담은 데이터 */
                }.bind(this)}>
               {/** <p><input type="text" name="title" value={data.title}/></p>
                    <p><textarea name="desc" value={data.desc}/></p>
                    Warning: You provided a `value` prop to a form field without an `onChange` handler. This will render a read-only field.
                    props 데이터를 그냥 쓰면 readonly로 걸려버림*/}
                    <p>
                        <input type="text" name="title"
                               value={this.state.data.title}
                               onChange={function (e) {

                        }}/>
                    </p>
                    <p><textarea name="desc" value={this.state.data.desc}/></p>
                    <p><input type="submit"/></p>
                </form>
            </article>
        );
    }
}

export default UpdateContent;
